import React from 'react';
import Joi from 'joi-browser';
import Link from 'next/link';
import Input from '../input/index';

class SignUp extends React.Component {

      state = {
        signUp:{
          firstName: '',
          lastName: '',
          email: '',
          password: '',
          confirmPassword:''
              },
        errors: {}  
      };   

      // state = {
      //     firstName: '',
      //     lastName: '',
      //     email: '',
      //     password: '',
      //     confirmPassword:'' 
      // };  

      schema = {
        firstName: Joi.string().required().label('First Name'),
        lastName: Joi.string().required(),
        // email: Joi.email({ minDomainAtoms: 2 }).required(), 
        password: Joi.string().required(),
        confirmPassword: Joi.string().required()
      }

      validate = () =>{
        const result = Joi.validate(this.state.signUp, this.schema, {abortEarly:false}
        );
        if(!result.error) return null;

        const errors= {};
        for (let item of result.error.details)
        errors[item.path[0]] = item.message;
        return errors;
      };

handleSubmit= e =>{
  e.preventDefault();

  const errors = this.validate()
//Call to Server 
console.log("SignUp Form Submitted");
}; 

  //   handleChange= event => {
  //     this.setState({[event.target.name]: event.target.value});
  //  }

//   handleSignUp = () => {
//     console.log("First Name: " + this.state.firstName);
//     console.log("Last Name: " + this.state.lastName);
//     console.log("Email: " + this.state.email);
//     console.log("Password: " + this.state.password);
//     console.log("Confirm Password: " + this.state.confirmPassword);
//  }

  handleChange= e =>{
    const signUp = {...this.state.signUp};
    signUp[e.currentTarget.name] = e.currentTarget.value; 
    this.setState({signUp});
  }

  handleSignUp = () => {
    console.log("First Name: " + this.state.signUp.firstName);
    console.log("Last Name: " + this.state.signUp.lastName);
    console.log("Email: " + this.state.signUp.email);
    console.log("Password: " + this.state.signUp.password);
    console.log("Confirm Password: " + this.state.signUp.confirmPassword);
 }

render(){
      const {signUp} =this.state;

    return (
        <React.Fragment>
          <div className="container">
            <h1 className="text-center">
              SignUp Page
            </h1>

            <form onSubmit={this.handleSubmit}>
              <Input 
                name="firstName"
                value={signUp.firstName}
                label="First Name"
                onChange={this.handleChange}
              />

              <Input
                name="lastName"
                value={signUp.lastName}
                label="Last Name"
                onChange={this.handleChange}
              />

              <Input
                name="email"
                value={signUp.email}
                label="Email Id"
                onChange={this.handleChange}
              />

              <Input
                name="password"
                value={signUp.password}
                label="Password"
                onChange={this.handleChange}
              />

              <Input
                name="confirmPassword"
                value={signUp.confirmPassword}
                label="Confirm Password"
                onChange={this.handleChange}
              />

              <Link href="/login">
                <button type="submit" className="btn btn-primary">Submit [Real]</button>
              </Link>
              <button type="submit" className="btn btn-primary" onClick={this.handleSignUp}>
                Submit For Data
              </button>
              <Link href="/login">
                <button type="submit" className="btn btn-primary">Already A Member</button>
              </Link>
            </form>
          </div>
        </React.Fragment>
      );
    }
  }

export default SignUp;
